sp_widget.client_script:
function() {
  /* widget controller */
  var c = this;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	system=[];
	data.list=[];

	// Get system list and Type
	var syst = new GlideAggregate("x_agsa_sapcockpit_last_metric");
	syst.addAggregate('COUNT', 'sid');
	syst.query();
	while(syst.next()) {
		objs={};
		objs.sid=syst.sid.toString();
		if ((syst.sid.indexOf('H') > -1 ) && (syst.sid.indexOf('H')) <2 )
			objs.type="HANA";
		else 
			objs.type="NETWEAVER";
		var comp = new GlideRecord("x_agsa_sapcockpit_last_metric");
		comp.get('sid',syst.sid.toString());
		objs.company=comp.company.getDisplayValue();
		system.push(objs);
	}

	// Now For each system, we need company, operational status, action, worst alarm severity
	for (var i=0;i<system.length;i++) {
		var obj={};
		var getSev = new GlideRecord("x_agsa_sapcockpit_alarm");
		getSev.addQuery('sid',system[i].sid);
		getSev.addQuery('acknowledge','false');
		getSev.orderByDesc('severity_code');
		getSev.query();
		if (getSev.next()) {
			obj.sid=system[i].sid.toString();
			obj.company=system[i].company;
			obj.type=system[i].type;
			obj.severity=getSev.severity.toString();
		} else {
			obj.sid=system[i].sid;
			obj.company=system[i].company;
			obj.type=system[i].type;
			obj.severity='CLEAR';
		}
		// Get Last Alarm
		var lastAlm = new GlideRecord("x_agsa_sapcockpit_alarm");
		lastAlm.addQuery('sid',system[i].sid);
		lastAlm.addQuery('acknowledge','false');
		lastAlm.orderByDesc('sys_updated_on');
		lastAlm.query();
		if (lastAlm.next()) {
			obj.lastAlm=lastAlm.sys_updated_on.getDisplayValue();
			// Add color depending on delay for Alarms
			//obj.value=compareDate(lastAlm.sys_updated_on,86400000);
			obj.value=true;
		} else {
			obj.lastAlm='No Alarms';
			obj.value=true;
		}
		// Get Last Metric
		var lastUpd2 = new GlideRecord("x_agsa_sapcockpit_last_metric");
		lastUpd2.addQuery('sid',system[i].sid);
		lastUpd2.orderByDesc('sys_updated_on');
		lastUpd2.query();
		lastUpd2.next();
		obj.lastMetr=lastUpd2.sys_updated_on.getDisplayValue();
		// Add color depending on delay for Alarms
		obj.value2=compareDate(lastUpd2.sys_updated_on,86400000);
		// Add color depending on delay for Metrics
		data.list.push(obj);
	}

	function compareDate(date,threshold) {
		var value=false;
		var gdt1=new GlideDateTime();
		gdt1.setValue(date);
		var gdt2=new GlideDateTime();
		var dur = GlideDateTime.subtract(gdt1, gdt2);
		if (dur.getNumericValue() < 86400000) 
			value=true;
		return value;
	}
})();
